import React, { Component } from 'react';
import './App.css';
import Display from './Display.js';

class App extends Component {
// state
  constructor(props){
    super(props);
    this.state = {
      currentInput1: "",
      currentInput2: "",
      currentInput3: "",
      currentInput4: ""
    }
  }

// function
  inputChanged1 = (event) => {
    let inputValue = event.target.value;
    this.setState({currentInput1:inputValue});
    console.log(inputValue);
  }

  inputChanged2 = (event) => {
    let inputValue = event.target.value;
    this.setState({currentInput2:inputValue});
    console.log(inputValue);
  }

  inputChanged3 = (event) => {
    let inputValue = event.target.value;
    this.setState({currentInput3:inputValue});
    console.log(inputValue);
  }

  inputChanged4 = (event) => {
    let inputValue = event.target.value;
    this.setState({currentInput4:inputValue});
    console.log(inputValue);
  }

// render
  render() {
    return (
      <div className="App">
       <h3>What is the Name of Your Next Horror Movie?</h3>
       <p>1. Invasion of the <input onInput={this.inputChanged1} type="text" placeholder='NOUN'/> Snatchers</p>
       <p>2. Last <input onInput={this.inputChanged2} type="text" placeholder='NOUN'/> on the Left </p>
       <p>3. The Hunch <input onInput={this.inputChanged3} type="text" placeholder='PART OF THE BODY'/> of 
       Notre <input onInput={this.inputChanged4} type="text" placeholder='NOUN'/></p>

       <Display></Display>

      </div>
    );
  }
}

export default App;
