import React, { Component } from 'react';
import './App.css';

class App extends Component {
// state
  constructor(props){
    super(props);
    this.state = {
      currentInput1: "",
      currentInput2: "",
      currentInput3: "",
      currentInput4: ""
    }
  }

// function
  inputChanged1 = (event) => {
    console.log("works");
    let inputValue = event.target.value;
    this.setState({currentInput1:inputValue});
    console.log(inputValue);
  }

// render
  render() {
    return (
      <div className="App">
       <h3>What is the Name of Your Next Horror Movie?</h3>
       <p>1. Invasion of the <input onInput={this.inputChange1} type="text" placeholder='NOUN'/> Snatchers</p>
       <p>2. Last <input type="text" placeholder='NOUN'/> on the Left </p>
       <p>3. The Hunch <input type="text" placeholder='PART OF THE BODY'/> of 
       Notre <input type="text" placeholder='NOUN'/></p>
       <button>SUBMIT</button>
      </div>
    );
  }
}

export default App;
